C++Python <pre><code>int** Transpose(int rows, int cols)
{
    int newRows = cols;
    int newCols = rows;

    // Khai báo mảng mới, cấp phát bộ nhớ cho các hàng
    int** aTranspose = new int*[newRows];

    // Duyệt mảng a theo hàng
    for (int r = 0; r &lt; newRows; r++)
    {
        // Cấp phát bộ nhớ cho các cột trong từng hàng
        aTranspose[r] = new int[newCols];

        for (int c = 0; c &lt; newCols; c++)
        {
            aTranspose[r][c] = a[c][r];
        }
    }

    return aTranspose;
}
</code></pre> <pre><code>def transpose_matrix(m):
    rows = len(m)
    cols = len(m[0])

    m_transpose = [[m[r][c] for r in range(rows)] for c in range(cols)] 
    return m_transpose
</code></pre>

C++Python <pre><code>ui ceiling_power_of_two(ui n)
{
    if (n == 0) return 1;

    n--;

    n |= n &gt;&gt; 1;
    n |= n &gt;&gt; 2;
    n |= n &gt;&gt; 4;
    n |= n &gt;&gt; 8;
    n |= n &gt;&gt; 16;

    n++;

    return n;
}
</code></pre> <pre><code>def ceiling_power_of_two(n):
    if n == 0:
        return 1

    n -= 1

    n |= n &gt;&gt; 1
    n |= n &gt;&gt; 2
    n |= n &gt;&gt; 4
    n |= n &gt;&gt; 8
    n |= n &gt;&gt; 16

    n += 1

    return n
