            if (next.r &gt;= 0 &amp;&amp; next.r &lt; R &amp;&amp; next.c &gt;= 0 &amp;&amp; next.c &lt; C &amp;&amp; grid[next.r][next.c] &gt; 0 &amp;&amp; !visited[next.r][next.c])
